import React, {useState} from "react";

function EditOrderPage({order}){
    const [name, setName] = useState("")
    const orderItems = order.order_items.map(item => {return {...item.product, quantity: item.quantity}})
    return (
        <div key={item.id} className="order-confirmation-page">
            <h1>Edit Order</h1>
            <div className="cart">
                <ul className="cart-items">
                    {orderItems.map(item => 
                        <p key={item.id} className="cart-item"> 
                            <button className="remove-button">‚ùå</button>
                            {item.name} (${parseFloat(item.price).toFixed(2)})  <span className="amount">x{item.quantity}</span> 
                        </p>
                    )}
                </ul>
                <h2 className="total">Total: ${parseFloat(orderItems.reduce((total, item) => total + item.price * item.quantity, 0)).toFixed(2)}</h2>
                <form className="name-form">
                    <input 
                        className="enter-name" 
                        id="name" 
                        type="text"
                        value={order.customer.name}
                        disabled={true}
                    />
                </form>
                <button disabled={name.length < 1 || orderItems.length < 1} className="order-button">
                    Save Changes
                </button>
            </div>
        </div>
    )
}

export default EditOrderPage
