import React, {useState, useEffect} from "react";
import Header from "./Header";
import Menu from "./Menu";
import Cart from "./Cart";
import OrderHistoryPage from "./OrderHistoryPage";

function App() {

  const [isViewingOrderHistory, setIsViewingOrderHistory] = useState(false)
  const [orderHistory, setOrderHistory] = useState([])
  const [products, setProducts] = useState([])
  const [cartItems, setCartItems] = useState([])

  useEffect(() => {
    fetch("http://localhost:9292/products")
      .then(r => r.json())
      .then(setProducts)
      .catch(() => alert("ERROR: no connection"))

    fetch("http://localhost:9292/orders")
      .then(r => r.json())
      .then(setOrderHistory)
      .catch(() => alert("ERROR: no connection"))
  }, [])

  function modifyCart(product, increaseItem){
    const index = cartItems.findIndex(item => item.id === product.id)
    console.log(cartItems)
    if(index === -1 && increaseItem){
      setCartItems([...cartItems, {...product, quantity: 1}])
      return
    }
    let newCart = [...cartItems]
    if(increaseItem){
      newCart[index].quantity++
      setCartItems(newCart)
    }else{
      setCartItems(cartItems.filter(item => item.id !== product.id))
    }
  }

  function onOrder(order){
    setOrderHistory([...orderHistory, order])
  }

  function cancelOrder(order_id){
    fetch(`http://localhost:9292/orders/${order_id}`, {
      method: "DELETE"
    })
      .then(setOrderHistory(orderHistory.filter(order => order.id !== order_id)))
      .catch(() => alert("ERROR: no connection"))
  }

  function seeOrders(bool){
    setIsViewingOrderHistory(bool)
  }

  return (
    <>
      <Header
        seeOrders={seeOrders}
      />
      { isViewingOrderHistory ?
        <OrderHistoryPage
          orderHistory={orderHistory}
          cancelOrder={cancelOrder}
        />
      :
        <div className="main">
          <Menu
            products={products}
            modifyCart={modifyCart}
          />
          <Cart
            modifyCart={modifyCart}
            cartItems={cartItems}
          />
        </div>
      }
    </>
  );
}

export default App;
