import React, {useState, useEffect} from "react";
import OrderListing from "./OrderListing";

function OrderHistoryPage({history, setHistory}){

    const orders = Object.values(history).flat(1)

    function cancelOrder(id){
        fetch(`http://localhost:9292/orders/${id}`, {
            method: 'DELETE'
        })
            .then(r => r.json())
            .then(order => {
                let newRecord = {[order.customer.name]:history[order.customer.name].filter(o => o.id !== order.id)}
                setHistory({...history, newRecord})
            })
    }

    return (
        <>
            <h1>Order History</h1>
            <div className="order-history">
                {/* {
                    .map( => 
                        <OrderListing 
                            key={order.id}
                            order={order}
                            cancelOrder={cancelOrder}
                            history={history}
                        />
                    )
                } */}
            </div>
        </>
    )
}

export default OrderHistoryPage
