import React from "react";

function OrderConfirmationPage({cartItems, onOrder}){
    return (
        <div className="order-confirmation-page">
            <h1>Confirm Order</h1>
            <h2>Items in Cart:</h2>
            <div className="cart">
            <h1>Cart</h1>
            <ul className="cart-items">
                {cartItems.map(item => 
                    <p key={item.id} className="cart-item"> <button className="remove-button" onClick={() => modifyCart(item, false)}>‚ùå</button> {item.name} <span className="amount">x{item.quantity}</span> 
                    </p>
                )}
            </ul>
            <h2 className="total">Total: ${parseFloat(cartItems.reduce((total, item) => total + item.price * item.quantity, 0)).toFixed(2)}</h2>
        </div>
        </div>
    )
}

export default OrderConfirmationPage
