import React from "react";

function Cart({changePage, cart, removeFromCart}) {
    return (
        <div className="cart">
            <h1>Cart</h1>
            <ul className="cart-items">
                {cart.map(item, count => 
                    <p key={item.id} className="cart-item"> 
                        <button className="remove-button" onClick={() => removeFromCart(item)}
                            >‚ùå</button> {item.name} <span className="amount">x{count}</span> 
                    </p>
                )}
            </ul>
            <h2 className="total">Total: ${parseFloat(cart.reduce((total, item) => total + item.price * item.quantity, 0)).toFixed(2)}</h2>
            <button disabled={cart.length < 1} className="order-button" onClick={() => changePage('orderConfirmation')}>Checkout</button>
        </div>
    )
}

export default Cart
