import React, {useState, useEffect} from "react";
import Header from "./Header";
import Catalog from "./Catalog";
import Cart from "./Cart";
import OrderHistoryPage from "./OrderHistoryPage";
import OrderConfirmationPage from "./OrderConfirmationPage";

function App() {
  const [history, setHistory] = useState([])
  const [currentPage, setCurrentPage] = useState('catalog')
  const [products, setProducts] = useState([])
  const [cart, setCart] = useState({})

  useEffect(() => {
    fetch("http://localhost:9292/products")
      .then(r => r.json())
      .then(setProducts)
      .catch(() => alert("ERROR: could not connect"))

    fetch("http://localhost:9292/customers")
      .then(r => r.json())
      .then((data) => {
        let history = {}
        for(const customer of data){
          history = {...history, [customer.name]:customer.orders}
        }
        setHistory(history)
      })
  }, [])

  function updateCart(product, amount){
    let updatedItemCount = (cart[product.id] ? cart[product.id].quantity : 0) + amount
    let updatedCart = {...cart, [product.id]:{...product, quantity:updatedItemCount}}
    if(updatedItemCount < 1){
      delete updatedCart[product.id]
    }
    setCart(updatedCart)
  }

  function updateHistory(order){
    setHistory({...history, [order.customer_id]:order})
    console.log(history)
  }

  function changePage(page){
    setCurrentPage(page)
  }

  const page = () => {
    switch(currentPage){
      default:
        return <div className="main">
          <Catalog
            products={products}
            updateCart={updateCart}
          />
          <Cart
            cart={cart}
            updateCart={updateCart}
            changePage={changePage}
          />
        </div>
      case 'history':
        return <OrderHistoryPage
          history={history}
          setHistory={setHistory}
        />
      case 'orderConfirmation':
        return <OrderConfirmationPage
          cart={cart}
          setCart={setCart}
          updateCart={updateCart}
          changePage={changePage}
          updateHistory={updateHistory}
        />
    }
  }

  return (
    <>
      <Header
        changePage={changePage}
      />
      {
        page()
      }
    </>
  );
}

export default App;
