import React, {useState, useEffect} from "react";
import Header from "./Header";
import Catalog from "./Catalog";
import Cart from "./Cart";
import OrderHistoryPage from "./OrderHistoryPage";
import OrderConfirmationPage from "./OrderConfirmationPage";

function App() {

  const [currentPage, setCurrentPage] = useState('catalog')
  const [orderHistory, setOrderHistory] = useState([])
  const [products, setProducts] = useState([])
  const [cart, setCart] = useState({})

  useEffect(() => {
    fetch("http://localhost:9292/products")
      .then(r => r.json())
      .then(products => {
        setProducts(products)
      })
      .then(
        fetch("http://localhost:9292/orders")
          .then(r => r.json())
          .then(setOrderHistory)
      )
      .catch(() => alert("ERROR: no connection"))
  }, [])

  function updateCart(product, amount){
    let updatedItemCount = (cart[product.id] ? cart[product.id].quantity : 0) + amount
    let updatedCart = {...cart, [product.id]:{...product, quantity:updatedItemCount}}
    if(updatedItemCount < 1){
      delete updatedCart[product.id]
    }
    setCart(updatedCart)
  }

  async function placeOrder(name){
    let order = null
    const getCustomer = await fetch(`http://localhost:9292/customers/${name}`)
    const customer = await getCustomer.json()
    if(customer){

    }
  }

  function changePage(page){
    setCurrentPage(page)
  }

  const page = () => {
    switch(currentPage){
      default:
        return <div className="main">
          <Catalog
            products={products}
            updateCart={updateCart}
          />
          <Cart
            cart={cart}
            updateCart={updateCart}
            changePage={changePage}
          />
        </div>
      case 'orderHistory':
        return <OrderHistoryPage
          setOrderHistory={setOrderHistory}
          orderHistory={orderHistory}
        />
      case 'orderConfirmation':
        return <OrderConfirmationPage
          cart={cart}
          updateCart={updateCart}
          placeOrder={placeOrder}
        />
    }
  }

  return (
    <>
      <Header
        changePage={changePage}
      />
      {
        page()
      }
    </>
  );
}

export default App;
