import React from "react";

function Cart({onOrder, cartItems}) {
    return (
        <div className="cart">
            <h1>Cart</h1>
            <ul className="cart-items">
                {[...new Set(cartItems)].map(item => 
                    <li key={item.id} className="cart-item">{item.name} <span className="amount">x{cartItems.filter(i => i.id === item.id).length}</span> 
                        <button className="remove-button">‚ùå</button>
                    </li>
                )}
            </ul>
            <h2 className="total">Total: ${parseFloat(cartItems.reduce((total, item) => total + item.price, 0)).toFixed(2)}</h2>
            <button className="order-button">Place Order</button>
        </div>
    )
}

export default Cart
